  <div className="row d-flex justify-content-center">
    <div className="col-2" />
    <div className="row d-flex justify-content-center">
      <img style={{ width: "1050px" }} src={IMG_LOGO_NP} />
    </div>
    <div className="col-2" />
  </div>
);
