import { get, groupBy } from "lodash";
import React from "react";
import { IMG_LOGO_RP_HORIZONTAL } from "./images";
import { formatSignatoriesPosition } from "./functions";
export const fullWidthStyle = {
  width: "100%",
  height: "auto"
};
export const signatureTextStyle = {
  color: "#090",
  fontWeight: "bold",
  fontSize: "1.1rem"
};
export const printCertStyle = {
  fontWeight: "bold",
  fontSize: "1.4rem",
  color: "#555",
  textAlign: "left"
};
export const SignatureTextStyle = {
  fontFamily: "Arial",
  fontSize: "1.4rem"
};
export const SignatureDateTextStyle = {
  fontFamily: "Arial",
  fontSize: "1.0rem"
};
export const printTextStyle = {
  fontWeight: "500!important",
  fontSize: "1.2rem",
  textDecoration: "none",
  color: "#555",
  textAlign: "left"
};
export const nameTextStyle = {
  fontSize: "3rem",
  textAlign: "center"
};
export const titleTextStyle = {
  fontWeight: "bold",
  fontSize: "3rem",
  textDecoration: "underline",
  textAlign: "center"
};
export const imageStyle = {
  display: "block",
  marginLeft: "auto",
  marginRight: "auto",
  width: "100%",
  height: "auto"
};
export const fullTableWidthStyle = {
  width: "100%",
  border: "1px solid black",
  height: "auto"
};
export const thStyle = {
  padding: "2px 5px",
  border: "1px solid black",
  textAlign: "left"
};
export const renderHeader = () => (
  <div className="row">
    <div className="col-7">
      9 Woodlands Avenue 9, Sinagpore 738964
      <br />
      tel: (65) 6510 3000
      <br />
