export const renderSkill = (skill, skillId, varDType, remarks) => {
  const sem = get(skill, "[0].competencyLevel");
  const sType = sem.substring(0, 1);
  const sem1 = sem.substring(1);
  const isArchievement = sType === "A" ? true : false;
  if (sType !== varDType.t) {
    varDType.t = sType;
  }
  let isChangeCompetency = false; 
  let oldCompetency = "-";  
  let oldCompetencyDesc = "";  
  let competency = "-"; 
  let competencyDesc = "";  
  let iCnt = 0;  
  let iTotal = -1;  
  // map 
  skill.forEach((e)=>{
     iTotal ++;     
   }); 
   console.log(iTotal);  
  const skillRows = skill.map((s, i) => {
    const compNo = s.competencyDescription.substring(0, 1);

    isChangeCompetency = competency === s.competencyDescription ? false : true;  
    if (!isArchievement) { 
      // console.log("Not archievement"); 
        if (isChangeCompetency ){ 
             //console.log("change compentency"); 
             oldCompetency = competency; 
             oldCompetencyDesc = competencyDesc; 
             competency = s.competencyDescription; 
             competencyDesc = s.competencyLevelDescription; 
             iCnt = iCnt + 1; 
        }  else    
        {  
             competencyDesc = competencyDesc.concat ("\n", s.competencyLevelDescription); 
        }
        // handle the last category and row.  
        if (iTotal === i) {
             oldCompetency = competency; 
             oldCompetencyDesc = competencyDesc;   
             isChangeCompetency = true;  
        }
    }
    return isArchievement ? (
      <tr key={i} style={thStyle}>
      <td style={thStyle}>
          {s.competencyDescription}
           {compNo === "1" && isArchievement ? (
            <ul><li>Process ten prescriptions of low complexity, with not more than 6 items</li><li>Handle at least five simple intervention with documentation: Able to explain clearly the purpose of the call, read back and document the intervention</li></ul>
          ) : null}
          {compNo === "2" && isArchievement ? (
            <ul><li>Dispense ten prescriptions of low complexity, with not more than 6 items</li><li>Handle at least five simple intervention with documentation: Able to explain clearly the purpose of the call, read back and document the intervention</li></ul>
          ) : null}
          {compNo === "3" && isArchievement ? (
            <ul><li>Counsel patients/caregivers on ten drug or product enquiries with minimum of three acute medications and three chronic medications</li></ul>
          ) : null}
          {compNo === "4" && isArchievement ? (
            <ul><li>Process and dispense ten prescriptions of low complexity, with not more than 6 items, to demonstrate compliance to medication safety practices</li></ul>
          ) : null}
          {compNo === "5" && isArchievement ? (
            <span>On three separate occasions:<ul><li>Obtain individual patient demographic and medication use record</li><li>Perform physical count of medications and match against medication records</li><li>Perform up-to-date documentation of patient’s medication information in patient records</li></ul></span>
          ) : null}
                   {compNo === "6" && isArchievement ? (
            <ul><li>Handle at least five unique simple enquiries related to sale of General Sales List or Pharmacy-only item and be able to address all enquiries</li></ul>
          ) : null}
        </td>
        <td style={thStyle}>{s.competencyLevelDescription}</td>
      </tr>
      ) : i > 0 ? isChangeCompetency || (i === iTotal)?  (
             <tr key={i} style={thStyle}>
             <td style={thStyle}>{oldCompetency}</td>
             <td style={thStyle}>{oldCompetencyDesc}</td>
             {iCnt === 2 ?  ( 
                  <td rowSpan="0" style={{width: "30%",textAlign: "left",padding: "2px 5px",border: "1px solid black"}}>
                  {remarks}
                   </td>
              ) : null}  
              </tr>
      ) : null : null;
  });
  return (
    <div className="col-12" key={skillId}>
      <div className="text-center">
        <p style={{textAlign: "left",fontSize: "1.2rem"}}>
          {!isArchievement ? (
            <p style={{ printTextStyle }}>
              Based on the student&apos;s performance, areas to be strengthened
              are listed in <strong>Table 2</strong>
              <br />
              <strong>
                Table 2: Areas to be reinforced for student upon on-boarding
              </strong>
            </p>
          ) : null}
        </p>
        <p style={{ textAlign: "left", fontWeight: "bold" }}>{sem1} </p>
        <table style={fullTableWidthStyle}>
          <tbody>
            <tr>
              <th style={{textAlign: "left",padding: "2px 5px",border: "1px solid black"}}>
                Key Tasks
              </th>
                   {isArchievement ? (
              <th style={{textAlign: "left",padding: "2px 5px",border: "1px solid black"}}>
                   Achievement
              </th>
                   ) : (
              <th style={{textAlign: "left",padding: "2px 5px",border: "1px solid black"}}>
                   Elements
              </th>
                   )}
              {!isArchievement ? (
                <th style={{textAlign: "left",padding: "2px 5px",border: "1px solid black"}}>
                  Remarks
                </th>
              ) : null}
            </tr>
            {skillRows}
          </tbody>
        </table>
      </div>
      <br />
    </div>
  );
};

export const renderLCA = (document, skills) => {
  // Get student info and course description
  const recipientName = get(document, "recipient.name");
  const recipientID = get(document, "recipient.studentId");
  const sText = get(document, "additionalData.performancerange").split("|");
  const Venue = sText[0];
  const DateRange = sText[1];
  const Remarks = sText[2];
  const groupedSkill = groupBy(skills, "competencyLevel");
  let varDType = { t: 0 };

  const renderedSkill = Object.keys(groupedSkill).map(skill =>
    renderSkill(groupedSkill[skill], skill, varDType, Remarks)
  );

  return (
    <div>
      <div
        className="row d-flex justify-content-center"
        style={{ marginTop: "3rem" }}
      />
      <div style={printTextStyle}>Letter of Competency Attainment</div>
      <div style={printTextStyle}>
        This is to inform that <strong>{recipientName} {recipientID}</strong> was trained and
        assessed based on the National Competency Standards for Pharmacy
        Technicians (Entry Level) in school (and through structured internship
        training at <strong>{Venue}</strong> from <strong>{DateRange}</strong>).
        <p>
          His performance based on the Keys Tasks of Pharmcy Technicial
          Entry-to-Practice (EPT) Competency Assessment is indicated in <strong>Table 1 </strong>
        </p>
        <p>
          <strong>
            Table 1: Achievements based on Key Tasks of Pharmacy Technician ETP
            Competency Assessment
          </strong>
        </p>
      </div>
      <div className="row">{renderedSkill}</div>
      <div className="row d-flex justify-content-center">
        <p style={printTextStyle}>.</p>
      </div>
    </div>
  );
};
