import { get } from "lodash";
import React from "react";
import { IMG_LOGO_RP_HORIZONTAL } from "./images";
import {
  formatDDMMMYYYY,
  formatDate,
  formatDatePrefix,
  formatCertName,
  formatSignatoriesPosition
} from "./functions";
export const fullWidthStyle = {
  width: "100%",
  height: "auto"
};
export const signatureTextStyle = {
  color: "#090",
  fontWeight: "bold",
  fontSize: "1.1rem"
};
export const printCertStyle = {
  fontFamily: "Old English Text MT",
  fontWeight: "bold",
  fontSize: "3rem",
  color: "#555",
  textAlign: "center"
};
export const SignatureTextStyle = {
  fontFamily: "Arial",
  fontSize: "1.4rem"
};
export const SignatureDateTextStyle = {
  fontFamily: "Arial",
  fontSize: "1.0rem"
};
export const printTextStyle = {
  fontFamily: "Times New Roman",
  fontWeight: "500!important",
  fontSize: "2rem",
  color: "#555",
  textAlign: "center"
};
export const nameTextStyle = {
  fontSize: "3rem",
  textAlign: "center"
};
export const titleTextStyle = {
  color: "rgb(30,93,200)",
  fontSize: "3rem",
  textAlign: "center"
};
export const imageStyle = {
  display: "block",
  marginLeft: "auto",
  marginRight: "auto",
  width: "100%",
  height: "auto"
};
export const renderHeader = () => (
  <div className="row">
    <div className="col-4">
      <img style={fullWidthStyle} src={IMG_LOGO_RP_HORIZONTAL} />
    </div>
    <div className="col" />
    <div className="col-7">
      <div style={titleTextStyle}>TRANSCRIPT OF ACADEMIC RECORD</div>
    </div>
  </div>
);
export const renderLCA = (document, skills) => {
  // Get student info and course description
  const recipientName = get(document, "recipient.name");
  const studentId = get(document, "recipient.studentId");
return (
  <div>
    <div
      className="row d-flex justify-content-center"
      style={{ marginTop: "3rem" }}
    />
    <div style={printTextStyle}>
      Letter of Competency Attainment
    </div>
    <div style={printTextStyle}>This is to inform that {recipientName} {recipientName} was trained and assessed based on the National Competency Standards for Pharmacy Technicians (Entry Level) in school (and through structured internship training at from).</div>

    <div className="row d-flex justify-content-center">
      <p style={printTextStyle}>.</p>
    </div>
  </div>
  );
};
// This is to display the content of letter of competency attainment
export const renderCompetency = document => {
  // Group all modules by courses
  const skills = get(document, "skills");
  const renderedCompetency = renderLCA(document, skills);
  return <div>{renderedCompetency}</div>;
};
// This is to display the SAS HOD signature 
export const renderOneSignature = certificate => {
  const certSign = formatSignatoriesPosition(
    get(certificate, "additionalData.certSignatories[0].position")
  );
  return (
    <div
      className="row d-flex justify-content-left align-items-end"
      style={{ marginTop: "0rem", marginBottom: "1rem" }}
    >
      <div className="col-6">
        <div className="px-6">
          <img
            style={{ width: "100%", borderBottom: "1px solid black" }}
            src={get(
              certificate,
              "additionalData.certSignatories[0].signature"
            )}
          />
        </div>
        <div className="text-left">
          <span style={SignatureTextStyle}>
            {get(certificate, "additionalData.certSignatories[0].name")}
            <br />
            {certSign[0]}
            <br />
            {get(certificate, "additionalData.certSignatories[0].organisation")}
            <br />
          </span>
          <br />
          <br />
        </div>
      </div>
    </div>
  );
};
