import { get } from "lodash";
import React from "react";
import { IMG_LOGO_RP_HORIZONTAL } from "./images";
import {
  formatDDMMMYYYY,
  formatSignatoriesPosition
} from "./functions";
export const fullWidthStyle = {
  width: "100%",
  height: "auto"
};
export const signatureTextStyle = {
  color: "#090",
  fontWeight: "bold",
  fontSize: "1.1rem"
};
export const printCertStyle = {
  fontFamily: "Old English Text MT",
  fontWeight: "bold",
  fontSize: "3rem",
  color: "#555",
  textAlign: "center"
};
export const SignatureTextStyle = {
  fontFamily: "Arial",
  fontSize: "1.4rem"
};
export const SignatureDateTextStyle = {
  fontFamily: "Arial",
  fontSize: "1.0rem"
};
export const printTextStyle = {
  fontFamily: "Times New Roman",
  fontWeight: "500!important",
  fontSize: "2rem",
  color: "#555",
  textAlign: "center"
};
export const nameTextStyle = {
  fontSize: "3rem",
  textAlign: "center"
};
export const titleTextStyle = {
  color: "rgb(30,93,200)",
  fontSize: "3rem",
  textAlign: "center"
};
export const imageStyle = {
  display: "block",
  marginLeft: "auto",
  marginRight: "auto",
  width: "100%",
  height: "auto"
};
export const renderHeader = () => (
  <div className="row">
    <div className="col-7">
    9 Woodlands Avenue 9, Sinagpore 738964<br />
    tel: (65) 6510 3000<br />
    <a href="https://www.rp.edu.sg">www.rp.edu.sg</a>
    </div>
    <div className="col" />
    <div className="col-4">
      <img style={fullWidthStyle} src={IMG_LOGO_RP_HORIZONTAL} />
    </div>
  </div>
);
// 
export const renderSkill = (skill, skillId, varDType) => {
   const skillRows = skill.map((s, i) => {
    const gradeDetails = s.competencyDescription.split("|");
    return (
      <tr key={i}>
        <td style={{ textAlign: "left" }}>{s.competencyDescription}
              {isEAE ? (
        <div>
          <p style={printTextStyle}>for achieving outstanding</p>
          <p style={printTextStyle}>EAE assessment performance</p>
        </div>
      ) : null}


        </td>
        <td style={{ textAlign: "left" }}>{s.competencyLevelDescription}</td>
      </tr>
    );
  });
  const sem = get(skillRows, "[0].competencyLevel");
  const sType = sem.substring(0, 1);
  let sSubTitle = "";
  const sem1 = sem.substring(1);
  if (sType !== varDType.t) {
    varDType.t = sType;
    sSubTitle = sType == "R" ? "EVENTS" : "MEMBERSHIP";
  }
  return (
    <div className="col-12" key={semesterId}>
      <div className="text-center">
        <p
          style={{
            textAlign: "left",
            fontSize: "1.2rem",
            fontWeight: "bold",
            textDecoration: "underline"
          }}
        >
          {sSubTitle}{" "}
        </p>
        <p style={{ textAlign: "left", fontWeight: "bold" }}>{sem1} </p>
        <table style={fullWidthStyle}>
          <tbody>
            <tr>
              <th style={{ width: "70%", textAlign: "left" }}>
                <u>Key Tasks</u>
              </th>
              <th style={{ width: "30%", textAlign: "left" }}>
                <u>Achievement</u>
              </th> 
            </tr>
            {skillRows}
          </tbody>
        </table>
      </div>
      <br />
    </div>
  );
};

export const renderLCA = (document, skills) => {
  // Get student info and course description
  const recipientName = get(document, "recipient.name");
  const studentId = get(document, "recipient.studentId");
  const sText = get(document, "additionalData.performancerange").split("|");
  const Venue = sText[0];
  const DateRange = sText[1];
  const Remarks = sText[2];  
  const Maxtask = 6;  
  const groupedSkill = groupBy(skills, "competencyLevel");
  let varDType = { t: 0 };

  const renderedSkill = Object.keys(groupedSkill).map(skill =>
    renderSemester(groupedSkill[skill], skill, varDType)
  );

return (
  <div>
    <div
      className="row d-flex justify-content-center"
      style={{ marginTop: "3rem" }}
    />
    <div style={printTextStyle}>
      Letter of Competency Attainment
    </div>
    <div style={printTextStyle}>This is to inform that {recipientName} {recipientName} was trained and assessed based on the National Competency Standards for Pharmacy Technicians (Entry Level) in school (and through structured internship training at <strong>{Venue}</strong> from <strong>{DateRange}</strong>).

   <p>His performance based on the Keys Tasks of Pharmcy Technicial Entry-to-Practice (EPT) Competency Assessment is indicated in <strong>Table 1</strong></p>

   <p><strong>Table 1: Achievements based on Key Tasks of Pharmacy Technician ETP Competency Assessment</strong></p>
    </div>
    <div className="row">{renderedSkill}</div>
    <div className="row d-flex justify-content-center">
      <p style={printTextStyle}>.</p>
    </div>
  </div>
  );
};
// This is to display the content of letter of competency attainment
export const renderCompetency = document => {
  // Group all modules by courses
  const skills = get(document, "skills");
  const renderedCompetency = renderLCA(document, skills);
  return <div>{renderedCompetency}</div>;
};
// This is to display the SAS HOD signature 
export const renderOneSignature = certificate => {
  const certSign = formatSignatoriesPosition(
    get(certificate, "additionalData.certSignatories[0].position")
  );
  return (
    <div
      className="row d-flex justify-content-left align-items-end"
      style={{ marginTop: "0rem", marginBottom: "1rem" }}
    >
      <div className="col-6">
        <div className="px-6">
          <img
            style={{ width: "100%", borderBottom: "1px solid black" }}
            src={get(
              certificate,
              "additionalData.certSignatories[0].signature"
            )}
          />
        </div>
        <div className="text-left">
          <span style={SignatureTextStyle}>
            {get(certificate, "additionalData.certSignatories[0].name")}
            <br />
            {certSign[0]}
            <br />
            {get(certificate, "additionalData.certSignatories[0].organisation")}
            <br />
          </span>
          <br />
          <br />
        </div>
      </div>
    </div>
  );
};
