import { get, groupBy } from "lodash";
import React from "react";
import { IMG_LOGO_RP_HORIZONTAL } from "./images";
import { formatSignatoriesPosition } from "./functions";
export const fullWidthStyle = {
  width: "100%",
  height: "auto"
};
export const signatureTextStyle = {
  color: "#090",
  fontWeight: "bold",
  fontSize: "1.1rem"
};
export const printCertStyle = {
  fontWeight: "bold",
  fontSize: "1.4rem",
  color: "#555",
  textAlign: "left"
};
export const SignatureTextStyle = {
  fontFamily: "Arial",
  fontSize: "1.4rem"
};
export const SignatureDateTextStyle = {
  fontFamily: "Arial",
  fontSize: "1.0rem"
};
export const printTextStyle = {
  fontWeight: "500!important",
  fontSize: "1.2rem",
  textDecoration: "none",
  color: "#555",
  textAlign: "left"
};
export const nameTextStyle = {
  fontSize: "3rem",
  textAlign: "center"
};
export const titleTextStyle = {
  color: "rgb(30,93,200)",
  fontSize: "3rem",
  textAlign: "center"
};
export const imageStyle = {
  display: "block",
  marginLeft: "auto",
  marginRight: "auto",
  width: "100%",
  height: "auto"
};
export const fullTableWidthStyle = {
  width: "100%",
  border: "1px solid black",
  height: "auto"
};
export const thStyle = {
  padding: "2px 5px",
  border: "1px solid black",
  textAlign: "left"
};
export const renderHeader = () => (
  <div className="row">
    <div className="col-7">
      9 Woodlands Avenue 9, Sinagpore 738964
      <br />
      tel: (65) 6510 3000
      <br />
      <a href="https://www.rp.edu.sg">www.rp.edu.sg</a>
    </div>
    <div className="col" />
    <div className="col-4">
      <img style={fullWidthStyle} src={IMG_LOGO_RP_HORIZONTAL} />
    </div>
  </div>
);
//
export const renderSkill = (skill, skillId, varDType, remarks) => {
  const sem = get(skill, "[0].competencyLevel");
  const sType = sem.substring(0, 1);
  const sem1 = sem.substring(1);
  const isArchievement = sType === "A" ? true : false;
  if (sType !== varDType.t) {
    varDType.t = sType;
  }
  let isChangeCompetency = false; 
  let oldCompetency = "-";  
  let oldCompetencyDesc = "";  
  let competency = "-"; 
  let competencyDesc = "";  
  let iCnt = 0;  
  // map 
  const skillRows = skill.map((s, i) => {
    const compNo = s.competencyDescription.substring(0, 1);
    isChangeCompetency = competency === s.competencyDescription ? true : false;  
    if (!isArchievement) { 
        if (isChangeCompetency){ 
             oldCompetency = competency; 
             oldCompetencyDesc = competencyDesc; 
             competency = s.competencyDescription; 
             competencyDesc = s.competencyLevelDescription; 
             iCnt = iCnt + 1; 
        }  else    
        {  
             competencyDesc = competencyDesc.concat ("<br />", s.competencyLevelDescription); 
        }
    }
    return isArchievement ? (
      <tr key={i} style={thStyle}>
      <td style={thStyle}>
          {s.competencyDescription}
           {compNo === "1" && isArchievement ? (
            <ul><li>Process ten prescriptions of low complexity</li><li>Handle at least five simple intervention</li></ul>
          ) : null}
          {compNo === "2" && isArchievement ? (
            <ul><li>Dispense ten prescription</li><li>Handle at five simple intervention with documentation</li></ul>
          ) : null}
          {compNo === "3" && isArchievement ? (
            <ul><li>Counsel patients</li></ul>
          ) : null}
          {compNo === "4" && isArchievement ? (
            <ul><li>Process and dispense ten</li></ul>
          ) : null}
          {compNo === "5" && isArchievement ? (
            <ul><li>On three separate occasions:Obtains individual patients</li><li>Perform physical count</li></ul>
          ) : null}
                   {compNo === "6" && isArchievement ? (
            <ul><li>Handle at least five unique</li></ul>
          ) : null}
        </td>
        <td style={thStyle}>{s.competencyLevelDescription}</td>
      </tr>
      ) : (
             <tr key={i} style={thStyle}>
             <td style={thStyle}>{oldCompetency}</td>
             <td style={thStyle}>{oldCompetencyDesc}</td>
             {iCnt === 1 ? ( 
                  <td rowSpan style={{width: "30%",textAlign: "left",padding: "2px 5px",border: "1px solid black"}}>
                  {remarks}
                   </td>
              ) : null}  
              </tr>
      );
  });
  return (
    <div className="col-12" key={skillId}>
      <div className="text-center">
        <p style={{textAlign: "left",fontSize: "1.2rem"}}>
          {!isArchievement ? (
            <p style={{ printTextStyle }}>
              Based on the student&apos;s performance, areas to be strengthened
              are listed in <strong>Table 2</strong>
              <br />
              <strong>
                Table 2: Areas to be reinforced for student upon on-boarding
              </strong>
            </p>
          ) : null}
        </p>
        <p style={{ textAlign: "left", fontWeight: "bold" }}>{sem1} </p>
        <table style={fullTableWidthStyle}>
          <tbody>
            <tr>
              <th style={{textAlign: "left",padding: "2px 5px",border: "1px solid black"}}>
                <u>Key Tasks</u>
              </th>
              <th style={{textAlign: "left",padding: "2px 5px",border: "1px solid black"}}>
                <u>Achievement</u>
              </th>
              {!isArchievement ? (
                <th style={{textAlign: "left",padding: "2px 5px",border: "1px solid black"}}>
                  <u>Remarks</u>
                </th>
              ) : null}
            </tr>
            {skillRows}
          </tbody>
        </table>
      </div>
      <br />
    </div>
  );
};

export const renderLCA = (document, skills) => {
  // Get student info and course description
  const recipientName = get(document, "recipient.name");
  const sText = get(document, "additionalData.performancerange").split("|");
  const Venue = sText[0];
  const DateRange = sText[1];
  const Remarks = sText[2];
  const groupedSkill = groupBy(skills, "competencyLevel");
  let varDType = { t: 0 };

  const renderedSkill = Object.keys(groupedSkill).map(skill =>
    renderSkill(groupedSkill[skill], skill, varDType, Remarks)
  );

  return (
    <div>
      <div
        className="row d-flex justify-content-center"
        style={{ marginTop: "3rem" }}
      />
      <div style={printTextStyle}>Letter of Competency Attainment</div>
